sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
	g_form.setMandatory("u_start_date", true);
	g_form.setMandatory("due_date", true);
   var dueDate = g_form.getValue("due_date");
	var startDate = g_form.getValue("u_start_date");
	var convertDueDate = new Date(dueDate);
	var convertStartDate = new Date(startDate);
	if(convertDueDate <= convertStartDate) {
		g_form.showFieldMsg("due_date", "Due date must be after Start date.", "error", false);
		return false;
	}
}
